class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        unordered_map<int,int> m1;
        map<int,int> m2;

        int n = nums.size();
        for(int i=0;i<n/2;i++)
        {
            m1[abs(nums[i] - nums[n-i-1])]++;
            int a = min(nums[i],nums[n-i-1]);
            int b = max(nums[i],nums[n-i-1]);

            int max_range = max(b,k-a);

            m2[max_range]++;
        }

        int res =INT_MAX;
        for(auto it = m1.begin();it!= m1.end();it++)
        {
            int tot = n/2;
            int ctr=0;
            tot -= it->second;

            auto itr = m2.lower_bound(it->first);
            int dist = distance(m2.begin(),itr);
            ctr += dist *2;
            tot -= dist;

            ctr += tot;

            res = min(ctr,res);
        }

        return res;
    }
};
