class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> um;
        vector<int> mx, mn;
        int n = nums.size();
        for(int i= 0; i<n/2; i++){
            um[abs(nums[i] - nums[n-i-1])]++;
            mx.push_back(max({nums[i], abs(k-nums[i]), nums[n-i-1], abs(k-nums[n-i-1])}));
            // mn.push_back(min({nums[i], abs(k-nums[i]), nums[n-i-1], abs(k-nums[n-i-1])}));
        }
        sort(mx.begin(), mx.end());
        sort(mn.begin(), mn.end());
        int ans = n;
        // cout<<"hello "<<endl;
        // for(auto x: mx) cout<<x<<" ";
        // cout<<endl;
        // for(auto x: mn) cout<<x<<" ";
        // cout<<endl;
        for(auto x: um){
            int lower= lower_bound(mx.begin(), mx.end(), x.first) - mx.begin();
            // cout<<lower<<endl;
            int singleChange = n/2 -lower - x.second;
            int doubleChange = lower*2;
            // cout<<x.first<<" "<<x.second <<" "<<singleChange<<" "<<doubleChange<<endl;
            ans = min(ans, singleChange + doubleChange);
        }
        return ans;
    }
};
