class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> sgfg;
        vector<int> kghg;

        int x = 0;
        while (x < nums.size() / 2) {
            int y = nums[x];
            int z = nums[nums.size() - x - 1];
            int g1 = abs(y - z);

            int g2 = max({y, z, k - y, k - z});

            sgfg[g1].push_back(g2);
            kghg.push_back(g2);
            ++x;
        }

        int b4 = nums.size();
        sort(kghg.begin(), kghg.end());

        auto b2 = sgfg.begin();
        while (b2 != sgfg.end()) {
            int g1 = b2->first;
            const vector<int>& n5 = b2->second;

            int j2 = lower_bound(kghg.begin(), kghg.end(), g1) - kghg.begin();
            j2 = j2 * 2 + (nums.size() / 2 - j2);

            int sg = 0;
            while (sg < n5.size()) {
                int kgh = n5[sg];
                if (kgh < g1) {
                    j2 -= 2;
                } else {
                    j2 -= 1;
                }
                ++sg;
            }

            b4 = min(b4, j2);
            ++b2;
        }

        return b4;
    }
};
