class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> freq;
        map<int,int> poss;
        int n = nums.size();
        for(int i = 0; i < nums.size()/2; i++) {
            int max_poss = max({abs(0-nums[i]), abs(k-nums[i]), abs(k-nums[n-i-1]), abs(0-nums[n-i-1])});
            int abs_diff = abs(nums[i] - nums[n-i-1]);
            freq[abs_diff]++;
            poss[max_poss]++;
        }
        int ans = 1e9;
        int prefix = 0;

        for(int i = 0; i <= (k+1)/2; i++) {
            ans = min(ans, (n/2)-freq[i]);
            prefix += poss[i] * 2;
        }
        for(int i = (k+1)/2 + 1; i <= k; i++) prefix += poss[i];

        for(int i = (k+1)/2 + 1; i <= k; i++) {
            prefix -= freq[i];
            ans = min(ans, prefix);
            prefix += poss[i];
            prefix += freq[i];
        }

        return ans;

    }
};
