class Solution(object):
    def minChanges(self, nums, k):
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """
        m={}
        v=[]
        for i in range(len(nums) // 2):
            a,b =nums[i], nums[len(nums)-i-1]
            diff=abs(a-b)
            X=max(a,b,k-a,k-b)
            if diff not in m:
                m[diff]=[]
            m[diff].append(X)
            v.append(X)
        ans=len(nums)
        v.sort()
        for diff, val in m.items():
            T=bisect.bisect_left(v,diff)*2 + (len(nums) // 2 - bisect.bisect_left(v,diff))
            for X in val:
                if X<diff:
                    T -= 2
                else:
                    T -= 1
            ans=min(ans, T)
        return ans
