class Solution {
public:
    int change(const vector<int>& nums, int k, int target) {
        if (target == -1)
            return INT_MAX;
        int n = nums.size();
        int ans = 0;
        for (int i = 0; i < n/2; i++) {
            int val1 = min(nums[i], nums[n-1-i]), val2 = max(nums[i], nums[n-1-i]);
            if (val2 - val1 == target)
                continue;
            else if (val2 - target >= 0 || val1 + target <= k)
                ans++;
            else
                ans += 2;
        }
        return ans;
    }

    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> m;
        int n = nums.size();
        for (int i = 0; i < n/2; i++) {
            int gap = abs(nums[i]-nums[n-1-i]);
            m[gap]++;
        }

        int val1 = 0, val2 = -1;
        int gap1 = 0, gap2 = -1;
        for (auto it : m) {
            if (it.second > val1 || (it.second == val1 && it.first < gap1)) {
                val2 = val1;
                val1 = it.second;
                gap2 = gap1;
                gap1 = it.first;
            } else if (it.second > val2 || (it.second == val2 && it.first < gap2)) {
                val2 = it.second;
                gap2 = it.second;
            }
        }
        // cout << val1 << " " << gap1 << " " << val2 << " " << gap2 << endl;
        return min(change(nums, k, gap1), change(nums, k, gap2));
    }
};
