class Solution {
public:
 using ll=long long;
    int minChanges(vector<int>& nums, int k) {
        ll n=nums.size();
        ll half=n/2;
        if(n==2)
        {
            return 0;
        }
        ll diff=0;
        map<ll,vector<ll>> m;
        vector<ll> a;
        for(ll i=0;i<half;i++)
        {  
            diff=abs(nums[i]-nums[n-i-1]);
            //if(diff<=k)
            {

               m[diff].push_back(max({nums[i],nums[n-i-1],abs(k-nums[i]),abs(k-nums[n-i-1])}));
               a.push_back(max({nums[i],nums[n-i-1],abs(k-nums[i]),abs(k-nums[n-i-1])}));
            }
        }
        sort(a.begin(),a.end());
        if(m.size()==1)
        {
            return 0;
        }
        ll siz=2*half;
        for(auto x:m)
        {

            ll f=x.first;
            auto s=x.second;
            auto p=lower_bound(a.begin(),a.end(),x.first);
            ll cnt=p-a.begin();
            ll fv=half+cnt;
            for(auto y:s){
                if(y>=x.first){
                    fv--;
                    siz=min(siz,fv);
                }
                else{
                    fv--;
                    fv--;
                    siz=min(siz,fv);
                }
            }


        }

         return siz;
    }
};
