class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> mp;
        vector<int> sol;
        int len = nums.size();
        for (int i = 0; i < len / 2; i++) {
            int first = nums[i];
            int second = nums[nums.size() - i - 1];
            int difference = abs(first - second);

             int X = max({first, second, k - first, k - second});

             mp[difference].push_back(X);
             sol.push_back(X);
        }

        int res = len;  
        sort(sol.begin(), sol.end());

        for (auto& [difference, value] : mp) {
            int Times = lower_bound(sol.begin(), sol.end(), difference) - sol.begin();
            Times = Times * 2 + (len / 2 - Times);

            for (auto& X : value) {
                if (X < difference) {
                    Times -= 2;
                } else {
                    Times -= 1;
                }
            }

            res = min(res, Times);
        }

        return res;
    }
};
