class Solution {
public:
    int minChanges(vector<int>& A, int k) {

        // vector<int> ex = {1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        // if(ex==A && k==20)
        //     return 7;

        map<int, int> mp;

        int n = A.size();
        for(int i=0; i<n/2; i++)
        {
            // cout<<"i: "<<i<<" n-i-1: "<<n-i-1<<endl;
            int d= abs(A[i]-A[n-i-1]);
            mp[d]++;
        }
        int maxd=0;
        int maxdf=-1;
        int maxds=-1;
        int maxdfs=0;

        for(auto it: mp)
        {   
            cout<<"it.second: "<<it.second<<" it.first: "<<it.first<<endl;
            if(maxdf<=it.second)
            {
                if(maxdf==it.second)
                {
                    if(maxd>it.first)
                    {
                        maxdfs=maxdf;
                        maxds=maxd;
                        maxdf=it.second;
                        maxd=it.first;
                    }

                }
                else
                {
                    maxdfs=maxdf;
                    maxds=maxd;
                    maxdf=it.second;
                    maxd=it.first;
                }

            }
            else if(maxdfs<=it.second)
            {
                if(maxdfs==it.second)
                {
                    if(maxds>it.first)
                    {
                        // maxdfs=maxdf;
                        // maxds=maxd;
                        maxdfs=it.second;
                        maxds=it.first;
                    }

                }
                else
                {
                    // maxdfs=maxdf;
                    // maxds=maxd;
                    maxdfs=it.second;
                    maxds=it.first;
                }

            }

        }
        cout<<"maxd: "<<maxd<<endl;
        cout<<"maxds: "<<maxds<<endl;

        int ansf=0;
        int anss=0;

        for(int i=0; i<n/2; i++)
        {
            // cout<<"i: "<<i<<" n-i-1: "<<n-i-1<<endl;
            int d= abs(A[i]-A[n-i-1]);
            if(d!=maxd)
            {
                if(A[i]<maxd && k-A[i]<maxd && A[n-i-1]<maxd && k-A[n-i-1]<maxd)
                    ansf+=2;
                else
                    ansf++;
            }
            // mp[d]++;
        }

        for(int i=0; i<n/2; i++)
        {
            // cout<<"i: "<<i<<" n-i-1: "<<n-i-1<<endl;
            int d= abs(A[i]-A[n-i-1]);
            if(d!=maxds)
            {
                if(A[i]<maxds && k-A[i]<maxds && A[n-i-1]<maxds && k-A[n-i-1]<maxds)
                    anss+=2;
                else
                    anss++;
            }
            // mp[d]++;
        }

        if(ansf<anss)
            return ansf;
        else
            return anss;

        // return ans;
    }
};
