class Solution {
public:
    map<int,vector<int>>mp;


    int surajsoln(vector<int>&nums,int k){

        vector<int>value;

        int n =nums.size();
        for (int i = 0; i < n / 2; ++i) {

            int first = nums[i];


            int last = nums[n - i - 1];


            int diff = abs(first-last);

             int element = max({first, last, k - first, k - last});

             mp[diff].push_back(element);


            value.push_back(element);
        }

        int ans = nums.size();  


        sort(value.begin(), value.end());

        for (const auto& [diff, values] : mp) {


            int T = lower_bound(value.begin(), value.end(), diff) - value.begin();
            T = T * 2 + (n / 2 - T);

            for (const auto& X : values) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

            ans = min(ans, T);
        }

        return ans;
    }
    int minChanges(vector<int>& nums, int k) {

        int result =surajsoln(nums,k);    

        return result;
    }
};
