class Solution {
public:
    int changes(vector<int>&nums, int diff, int k, int &currAns) {
        int i = 0, j = nums.size()-1;
        int ans = 0;
        while(i < j)    {
            if(abs(nums[i] - nums[j]) == diff)   {
                i++;
                j--;
                continue;
            }
            int maxi = max(nums[i], nums[j]);
            int mini = min(nums[i], nums[j]);
            if(maxi - diff >= 0 || mini + diff <= k)
                ans += 1;
            else
                ans += 2;
            if(ans > currAns)
                return ans;
            i++;
            j--;
        }
        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size(), i = 0, j = n-1;
        unordered_map<int,int>mpp;
        while(i < j)    {
            mpp[abs(nums[i] - nums[j])]++;
            i++;
            j--;
        }

        int maxOccur = INT_MIN;
        int maxElem = -1;
        for(auto itr: mpp)  {
            if(itr.second > maxOccur)   {
                maxOccur = itr.second;
                maxElem = itr.first;
            }
            if(itr.second == maxOccur)  {
                maxElem = min(itr.first, maxElem);
            }
        }
        int mini = INT_MAX;
        mini = changes(nums, maxElem, k, mini);
        for(auto itr: mpp)  {
            if((n/2)-itr.second < mini)
                mini = min(mini, changes(nums, itr.first, k, mini));
        }
        // i = 0, j = n-1;
