class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> cost(k + 1, 0);
        for(int i = 0; i < n / 2; ++i) {
            int a = nums[i], b = nums[n - i - 1];
            if(a > b) {
                swap(a, b);
            }

            cost[0]++;
            cost[b - a]--;
            if(b - a < k) {
                cost[b - a + 1]++;
            }
            int mx = max(b, k - a) + 1;
            if(mx <= k) {
                cost[mx]++;
            }
        }

        int ans = cost[0];
        for(int i = 1; i <= k ; ++i) {
            cost[i] += cost[i - 1];
            ans = min(ans, cost[i]);
        }
        return ans;
    }
};
