class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();

        map<int, vector<int>> temp ;
        vector<int>dyn ;
        for( int i =0 ; i<nums.size()/2; i++ ){
            int l = nums[i];
            int r = nums[n-i-1];
            int diff = abs(r-l);
            int maxdiff = max({r, l , k-l , k-r}) ;
            temp[diff].push_back(maxdiff);
            dyn.push_back(maxdiff);
        }

        int res =n ;
        sort(dyn.begin(),dyn.end());

        for(auto it : temp){
            int d = it.first ;
            int z = lower_bound(dyn.begin(),dyn.end(),d)-dyn.begin();
            z = z*2 + (n/2-z);
            for(auto i : it.second){
                if(i<d)z-=2;
                else z-=1;
            }
            res = min(res,z);
        }

        return res;


    }
};
