class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>>mp;
        vector<int> res;
        int n=nums.size();
        for(int i = 0;i<n/2;i++){
            int a = nums[i],b = nums[n-i-1];
            int x = max({a,b,k-a,k-b});
            mp[abs(a-b)].push_back(x);
            res.push_back(x);
        }
        int len = n;
        sort(begin(res),end(res));
        for(auto &it:mp){
            int diff = it.first;
            int ans = lower_bound(res.begin(),res.end(),diff)-res.begin();
            ans = ans*2+(n/2-ans);
            for(auto &x:it.second){
                if(x<diff){
                    ans--;
                    ans--;
                }
                else ans--;
            }
            len = min(len,ans);
        }
        return len;
    }
};
