class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mpp;
        vector<int> vec;
        int n= nums.size();
        for(int i = 0;i<n/2;i++){
            int a = nums[i];
            int b = nums[n-i-1];
            int diff = abs(a-b);
            int X = max({k-a, a,k-b,b});
            mpp[diff].push_back(X);
            vec.push_back(X);
        }
        int result = n;
        sort(vec.begin(),vec.end());
        for(auto &val:mpp){
            int diff = val.first;
            int ss = lower_bound(vec.begin(),vec.end(),diff)-vec.begin();
            ss = ss*2+(n/2-ss);
            for(auto &X:val.second){
                if(X>=diff)ss-=1;
                else ss-=2;
                }
            result = min(result,ss);
        }
        return result;
    }
};
