class Solution {
public:
    int mc = INT_MAX;
    int minChanges(vector<int>& v, int k) {
        vector<int>hash(k + 2);
        int rr = INT_MAX;
        int n = v.size();
        int sum = 0;
        for(int i = 0; i < n/2; i++){

            int miv = min(v[i], v[n-i-1]);
            int mav = max(v[i], v[n-i-1]);
            int cr = max(mav, k - miv);




            hash[cr + 1] += 2;
            hash[cr + 1] -= 1;
            hash[k+1] -= 2;
            hash[0]++;
            hash[mav - miv]--;
            hash[mav - miv + 1] += 1;

        }

        for(int i = 0; i <= k; i++){
            sum += hash[i];
            hash[i] = sum;
            rr = min(rr, hash[i]);
        }

        return rr;
    }
};
