class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        map<int,int>m;
        vector<int>dp(k+5,0);
        for(int i=0;i<n/2;i++){
            int d = abs(a[i]-a[n-i-1]);
            int mx = max(a[i],k-a[i]);
            int y=a[n-i-1];
            mx = max({mx,y,k-y});
                // cout<<d<<" "<<0<<" "<<mx<<" ";
            dp[0]++;
            dp[mx+1]--;
            dp[mx+1]+=2;
            dp[k+3]-=2;
            dp[d]--;
            dp[d+1]++;
        }
        int ans = dp[0];
         // cout<<dp[0]<<" ";
        for(int i=1;i<=k;i++){
            dp[i]+=dp[i-1];
             // cout<<dp[i]<<" ";
            ans = min(ans,dp[i]);
        }
        // cout<<dp[3]<<" ";

        return ans;
    }
};
