class Solution {
public:
    int minChanges(vector<int>& nms, int k) {
        // my own
        map<int,vector<int>> mmp;
        vector<int>chng;
        for(int i = 0;i<nms.size()/2;i++){
            int p = nms[i],q = nms[nms.size()-i-1];
            int cng = abs(p-q);
            int t = max({p,q,k-p,k-q});
            mmp[cng].push_back(t);
            chng.push_back(t);
        }
        int mnRqChng = nms.size();
        // sort the arrr->chng
        sort(chng.begin(),chng.end());
        for(auto &elm:mmp){
            int cng = elm.first;
            int r = lower_bound(chng.begin(),chng.end(),cng)-chng.begin();
            r = r*2+(nms.size()/2-r);
            for(auto &t:elm.second){
                if(t<cng) r-=2;
                else r-=1;
            }
            mnRqChng = min(mnRqChng,r);
        }
        // final ans
        return mnRqChng;
    }
};
