class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int> mp;
        multiset<int> mu;
        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-i-1])]++;
            int maxi=max(nums[i], nums[n-i-1]);
            int mini=min(nums[i], nums[n-i-1]);
            mu.insert(max({k-maxi, maxi, k-mini}));
        }
        int ans=INT_MAX;
        vector<int> v;
        for(auto it:mu){
            v.push_back(it);
        }

        for(int i=0;i<=k;i++){
            int left_diff_pairs=n/2-mp[i];
            int temp=0;
            temp+=left_diff_pairs;
            auto it=lower_bound(v.begin(), v.end(), i);
            int x=0;

            if(it!=v.begin()){
                it--;
                x=(it-v.begin())+1;
                temp+=x;
            }
              cout<<temp<<" ";


            ans=min(ans, temp);

        }
        cout<<endl;

        return ans;

    }
};
