class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
      map<int,vector<int>> mp;
        vector<int> ans;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i],b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            int X = max({a,b,k-a,k-b});
            mp[diff].push_back(X);
            ans.push_back(X);
        }
        int res = nums.size();
        sort(ans.begin(),ans.end());

        for(auto &val:mp){

            int diff = val.first;
            int t = lower_bound(ans.begin(),ans.end(),diff)-ans.begin();
            t = t*2+(nums.size()/2-t);

            for(auto &i:val.second){
                if(i<diff){
                    t-=2;
                }
                else{
                    t-=1;
                }
            }

            res = min(res,t);

        }

        return res;

    }
};
