class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int m = n / 2;
        vector<vector<int>> arr(m);

        unordered_map<int, int> freq;
        for(int i = 0; i < m; ++i){
            if(nums[i] < nums[n - i - 1])
                arr[i] = {nums[i], nums[n - i - 1], k - nums[i], nums[n - i - 1] - nums[i]};
            else
                arr[i] = {nums[n - i - 1], nums[i], k - nums[n - i - 1], nums[i] - nums[n - i - 1]};
            freq[arr[i][3]]++;
        }

        unordered_map<int, int> temp;
        for(auto it : freq){
            if(temp.find(it.second) == temp.end())
                temp[it.second] = it.first;
            else
                temp[it.second] = min(temp[it.second], it.first);
        }

        int mini = INT_MAX;
        // unordered_map<int, int> mp;
        for(auto it : temp){
            int diff = it.second;
            int steps = 0;
            // if(mp.find(diff) != mp.end())
            //     continue;

            for(int j = 0; j < m; ++j){
                if(arr[j][3] == diff)
                    continue;

                if(arr[j][2] >= diff || arr[j][1] >= diff)
                    steps += 1;
                else if(k >= diff)
                    steps += 2;
                else{
                    steps = 1e9;
                    break;
                }
            }
            mini = min(mini, steps);
            // mp[diff] = mini;
        }

        return mini;
    }
};
