class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int ans = n;
        unordered_map<int,int> diff;
        for(int i=0;i<n/2;i++){
            diff[abs(nums[i]-nums[n-i-1])]++;
        }
        priority_queue<pair<int,int>> pq;
        for(auto it:diff){
            pq.push({it.second,it.first});
        }
        while(!pq.empty()){
            int dd = pq.top().second;
            pq.pop();
            int op = 0;
            bool flag = true;
            for(int i=0;i<n/2;i++){
                if(abs(nums[i]-nums[n-i-1])!=dd){
                    int a = min(nums[i],nums[n-i-1]);
                    int b = max(nums[i],nums[n-i-1]);
                    if(a+dd<=k && a+dd>=0) op++;
                    else if(a-dd<=k && a-dd>=0) op++;
                    else if(b+dd<=k && b+dd>=0) op++;
                    else if(b-dd<=k && b-dd>=0) op++;
                    else if(dd<=k){
                        op+=2;
                    }
                    else{
                        flag = false;
                        break;
                    }
                }
            }
            if(!flag) continue;
            if(op<=ans) ans = op;
            else break;
        }
        return ans;
    }
};
