class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        //if(nums.size()==20 && k==20)return 7;
        unordered_map<int,vector<int>>mp;
        vector<int>temp;

        for(int i=0; i<nums.size()/2; i++){
            int a = nums[i];
            int b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            mp[diff].push_back(max({a,b,k-a,k-b}));
            temp.push_back(max({a,b,k-a,k-b}));
        }

        int ans = nums.size();
        sort(temp.begin(), temp.end());

        for(auto it: mp){
            int diff = it.first;
            int curr = lower_bound(temp.begin(), temp.end(), diff)-temp.begin();

            curr = curr*2 + (nums.size()/2-curr);

            for(auto val : it.second){
                if(val<diff){
                    curr-=2;
                }else{
                    curr--;
                }
            }

            ans = min(ans, curr);
        }

        return ans;
    }
};
