#define all(v) v.begin(),v.end()
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp;
        int n = nums.size();
        vector<int> v;
        for(int i = 0; i <  n /2; i++) {
            int a = nums[i], b = nums[n-i-1];
            int d = abs(a-b);
            int x = max({a,b,k-a,k-b});
            mp[d].push_back(x);
            v.push_back(x);
        }
        int ans = nums.size();
        sort(all(v));

        for(auto &p : mp) {
            int d = p.first;
            int t = lower_bound(all(v),d) - v.begin();
            t = t*2 + (nums.size()/2 - t);

            for(auto &x : p.second) {
                if(x < d) t -= 2;
                else t -= 1;
            }
            ans = min(ans, t);
        }
        return ans;
    }
};
