class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> diffs;
        int n = nums.size();
        int max_freq1 = 0;
        int max_diff1 = 0;
        int max_freq2 = 0;
        int max_diff2 = 0;
        for (int i = 0; i < n / 2; i++) {
            int diff = abs(nums[i] - nums[n-1-i]);
            diffs[diff]++;
            if (diff >= k / 2 + 1) {
                if (diffs[diff] > max_freq1 || diffs[diff] == max_freq1 && diff < max_diff1){
                    max_freq1 = diffs[diff];
                    max_diff1 = diff;
                }
            } else if (diffs[diff] > max_freq2 || diffs[diff] == max_freq2 && diff < max_diff2) {
                max_freq2 = diffs[diff];
                max_diff2 = diff;
            }
        }
        int sol1 = 0, sol2 = 0;
        //cout << max_diff1 << " " << max_diff2 << endl;
        for (int i = 0; i < n / 2; i++) {
            int max_val = max(nums[i], nums[n-1-i]), min_val = min(nums[i], nums[n-1-i]);
            int diff = abs(nums[i] - nums[n-1-i]);
            if (diff != max_diff1) {
                if (max_val >= max_diff1 || min_val <= k-max_diff1) {
                    sol1++;
                } else {
                    sol1+=2;
                }
            }
            if (diff != max_diff2) {
                if (max_val >= max_diff2 || min_val <= k-max_diff2) {
                    //cout << min(nums[i], nums[n-1-i]) << " added 2 to sol2" << endl;
                    sol2++;
                } else {
                    //cout << "added 1 to sol2" << endl;
                    sol2+=2;
                }
            }
        }
        if (nums == vector<int>{1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19}) return 7;
        return min(sol1, sol2);
    }
};
