class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums) // 2
        dct = {i: 0 for i in range(k + 1)}
        limits = []
        for i in range(n):
            diff = abs(nums[i] - nums[2 * n - 1 - i])
            dct[diff] = dct[diff] + 1
            limits.append(max(max(nums[i], nums[2 * n - 1 - i]),
                              max(k - nums[i], k - nums[2 * n - 1 - i])))
        limits = sorted(limits)
        idx = 0
        max_val = 0
        for i in range(k + 1):
            while idx < n and limits[idx] < i:
                idx += 1
            if dct[i] - idx > max_val:
                max_val = dct[i] - idx
        return n - max_val
