class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        h = Min = n // 2
        freq = defaultdict(int)

        for i in range(h):
            freq[abs(nums[i] - nums[n - i - 1])] += 1

        ordered = sorted((max(k - nums[i], k - nums[n - i - 1], nums[i], nums[n - i - 1]) for i in range(h)))

        for key, value in freq.items():
            pairs = bisect.bisect_left(ordered, key)
            current = (h - value - pairs) + pairs * 2
            Min = min(Min, current)

        return Min
