#define ll long long

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

       ll n = nums.size();

        vector<ll> temp;

        map<ll, vector<ll> > mp;

        for(ll i = 0; i< n/ 2;i++)
        {
            ll x = nums[i];
            ll y = nums[n-i - 1];
            ll maxi = max(max(x, y), max(k - x, k - y));
            temp.push_back(maxi);
            ll d = abs(x - y);
            mp[d].push_back(maxi);
        }

        sort( temp.begin(), temp.end());

        ll res = n;

        for(auto &i:mp)
        {
            ll d = i.first;
            ll l = 0;
            ll r = temp.size() - 1;
            ll ind  = n;
            ll mid = ( l + r)/2;

            while( l <= r)
            {
                mid = ( l + r)/2;

                if( temp[mid] >= d)
                {
                    ind = mid;
                    r = mid - 1;
                }
                else
                {
                    l = mid + 1;
                }
            }
            ll x = n/2 - ind;
            ind *= 2;
            ind += x;

            for(auto &j:i.second){
                if( j < d)
                {
                    ind -= 2;
                }

                else
                {
                    ind -=1;
                }
            }

            res = min( res, ind);

        }

        return res;


    }
};
