class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int cnt=0;
        int i=0,j=nums.size()-1;
        unordered_map<int,int>m;
        vector<int>pos(k+1);
        for(;i<j;i++,j--){
            cnt++;
            m[abs(nums[i]-nums[j])]++;
            pos[max({nums[i],nums[j],k-nums[i],k-nums[j]})]++;
        }
        for(int i=k;i>=0;i--){
            if(i+1<pos.size())
                pos[i]+=pos[i+1];
        }
        int ans=1e8;
        for(int i=0;i<=k;i++){
            int req=cnt;
            req-=m[i];
            int one=pos[i]-m[i];
            int to_remove=min(req,one);
            req-=to_remove;
            one-=to_remove;
            int ca=to_remove;
            if(req>0){
                ca+=req*2;
            }
            ans=min(ans,ca);
        }
        return ans;
    }
};
