class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int mn = nums.size();
        map<int,vector<int>> m;
        vector<int> v;
        for(int i = 0;i<nums.size()/2;i++){
            m[abs(nums[i]-nums[nums.size()-i-1])].push_back(max({nums[i],nums[nums.size()-i-1],k-nums[i],k-nums[nums.size()-i-1]}));
            v.push_back(max({nums[i],nums[nums.size()-i-1],k-nums[i],k-nums[nums.size()-i-1]}));
        }
        sort(v.begin(),v.end());
        for(auto &val:m){
            int p = lower_bound(v.begin(),v.end(),val.first)-v.begin();
            p=p*2+(nums.size()/2-p);
            for(int i=0;i<val.second.size();i++){
                if(val.second[i]<val.first) p-=2;
                else p--;
            }
            mn = min(mn,p);
        }
        return mn;
    }
};
