class Solution {
public:
    int minChanges(vector<int>& nums, int k) 
    {    
        vector<int> maxreach(k+1);
        map<int, int> diff;
        int n = nums.size();
        for(int i = 0 ; i < nums.size()/2 ; i++)
        {
            int max_diff = max(nums[i], nums[n-i-1]);
            max_diff = max(max_diff, k - min(nums[i], nums[n-i-1]));
            //cout << max_diff << endl;
            maxreach[max_diff]++;
            diff[abs(nums[i] - nums[nums.size()-i-1])]++;
        }
        //cout << endl;
        for(int i = k-1 ; i >= 0 ; i--)
            maxreach[i] += maxreach[i+1];
        // for(int i = 0 ; i <= k ; i++)
        //     cout << maxreach[i] << ", ";
        // cout << endl;
        int res = nums.size();

        for(auto &m : diff)
        {
            //cout << "cur = " << m.first << endl;
            int tmp = nums.size()/2 - m.second; // num of group 至少改變一個
            //cout << tmp << endl;

            tmp += n/2 - maxreach[m.first]; // 要變兩個
            //cout << ( n/2 - maxreach[m.first]) << endl << endl;
            res = min(res, tmp);
        }
        //cout << "-----------------------\n";
        return res;
    }
};

