class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
       int n = nums.size();
        map<int,vector<int>> mp;
        vector<int> v;
        for(int i = 0;i<n/2;i++){
            int a = nums[i],b = nums[n-i-1];
            int d = abs(a-b);
            int X = max({a,b,k-a,k-b});
            mp[d].push_back(X);
            v.push_back(X);
        }
        int ans = n;

        sort(v.begin(),v.end());

        for(auto &val:mp){

            int dif = val.first;
            int l = lower_bound(v.begin(),v.end(),dif)-v.begin();

            l = l*2+(n/2-l);

            for(auto &X:val.second){
                if(X<dif){
                    l-=2;
                }
                else{
                    l-=1;
                }
            }

            ans = min(ans,l);

        }
        for(int i=0;i<n;i++){
            continue;
        }

        return ans;

    }
};
