class Solution {
    public int minChanges(int[] nums, int k) {
        Map<Integer,Integer> map = new HashMap<>();
        PriorityQueue<int[]> pq = new PriorityQueue<>((a,b) -> {
            if(a[1] == b[1]){
                return a[0] - b[0];
            }else{
                return b[1] - a[1];
            }
        });
        for (int i = 0; i < nums.length/2; i++) {
            int abs = Math.abs(nums[i] - nums[nums.length - i - 1]);
            map.put(abs,map.getOrDefault(abs,0)+1);
        }
        for (Map.Entry<Integer, Integer> entry : map.entrySet()) {
            pq.offer(new int[]{entry.getKey(),entry.getValue()});
        }
        int diff = pq.poll()[0];
        int res = 0;
        for (int i = 0; i < nums.length/2; i++) {
            int abs = Math.abs(nums[i] - nums[nums.length - i - 1]);
            if(abs == diff){
                continue;
            }
            int min = Math.min(nums[i], nums[nums.length - i - 1]);
            int max = Math.max(nums[i], nums[nums.length - i - 1]);
            if(diff > abs){
                int num1 = diff - abs;
                if(min - num1 <= k && min - num1 >= 0 || max +num1 <= k){
                    res++;
                }else{
                    res+=2;
                }
            }else{
                int num2 = abs - diff;
                if(min + num2 <= k || (max - num2 >= 0 &&max - num2 <= k)){
                    res++;
                }else{
                    res+=2;
                }
            }
        }

        if(!pq.isEmpty()){
            int diff1 = pq.poll()[0];
            int res1 = 0;
            for (int i = 0; i < nums.length/2; i++) {
                int abs = Math.abs(nums[i] - nums[nums.length - i - 1]);
                if(abs == diff1){
                    continue;
                }
                int min = Math.min(nums[i], nums[nums.length - i - 1]);
                int max = Math.max(nums[i], nums[nums.length - i - 1]);
                if(diff1 > abs){
                    int num1 = diff1 - abs;
                    if(min - num1 <= k && min - num1 >= 0 || max +num1 <= k){
                        res1++;
                    }else{
                        res1+=2;
                    }
                }else{
                    int num2 = abs - diff1;
                    if(min + num2 <= k || (max - num2 >= 0 &&max - num2 <= k)){
                        res1++;
                    }else{
                        res1+=2;
                    }
                }
            }
            return Math.min(res,res1);
        }
        return res;
    }
}
