class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int i = 0 , n = nums.size(), ans = n / 2; 
        map<int,int> c ;
        vector<int> v ;
        while(i < n - 1 - i)
        {
            v.push_back(max({nums[i] , nums[n - 1 - i] , k - min(nums[i] , nums[n - i - 1])})) ;
            c[abs(nums[i] - nums[n - i - 1])] ++ ;
            i ++ ;
        }
        sort(v.begin() , v.end()) ;
        for(auto &[f , s] : c)
        {
            int it = (lower_bound(v.begin() , v.end() , f) - v.begin()) ;
            ans = min(ans , it * 2 + (n - s * 2 - it * 2) / 2) ;
        }
      return ans ;
    }
};
