func minChanges(nums []int, k int) int {
	n := len(nums)
	cnt := make(map[int]int, len(nums)/2)
	diff := make([]int, k+2)
	for i, j := 0, n-1; i < j; i, j = i+1, j-1 {
		cnt[abs(nums[i]-nums[j])]++
		m := max(k-nums[i], nums[i], k-nums[j], nums[j])
		diff[0]++
		diff[m+1]--
	}
	sum := 0
	ans := n
	for a := 0; a <= k; a++ {
		sum += diff[a]
		one := sum - cnt[a]
		two := n/2 - sum
		t := one + two*2
		ans = min(ans, t)
	}
	return ans
}

func abs[T int | int64 | int32 | int16 | int8 | float64](x T) T {
	if x < 0 {
		return -x
	}
	return x
}
