class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int>mp;
        int n = nums.size(), m = n/2, res, currCnt = 0, fin = INT_MAX, req = n/2;
        for(int i = 0; i< m;i++){
            int diff = abs(nums[i]-nums[n-i-1]);
            mp[diff]++;
            if(currCnt < mp[diff]){
                currCnt = mp[diff];
                res = diff;
            }
        }
        vector<int>v;
        for(auto [i,c]:mp){
            if(c==currCnt) v.push_back(i);
        }
        for(auto [e,cnt]:mp){
            int ans = 0;
            if(fin < (req-cnt)) continue;
            for(int i = 0;i<m;i++){
                int diff = abs(nums[i]-nums[n-i-1]), mini = min(nums[i],nums[n-i-1]), maxi = max(nums[i],nums[n-i-1]);
                if(diff == e) continue;
                if( (k - mini < e) && (maxi - 0 < e)) ans+=2;
                else ans+=1;
            }
            fin = min(fin,ans);
        }
        return fin;
    }
    // 1 2 0 2 4 3
    // 0 1 2 3 3 6 5 4 -> 
};
