class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> m;
        vector<int> ae;
        int n=nums.size(),res=n;
        for(int i = 0;i<n/2;i++){
            int diff = abs(nums[i]-nums[n-i-1]);
            int t=max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]});
            m[diff].push_back(t);
            ae.push_back(t);
        }
        sort(ae.begin(),ae.end());   
        for(auto &v:m){
            int diff = v.first;
            int t = lower_bound(ae.begin(),ae.end(),diff)-ae.begin();
            t = t*2+(n/2-t);
             for(auto &i : v.second){
                if(i<diff) t-=2;
                else t-=1;
            }
            res = min(res,t);
        }
        return res;
    }
};
