class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        unordered_map<int,vector<int>> mp;
        int n=a.size();
        vector<int>temp;
        for(int i=0;i<n/2;i++){
            mp[abs(a[i]-a[n-i-1])].push_back(max({a[i],a[n-i-1],k-a[i],k-a[n-i-1]}));
            temp.push_back(max({a[i],a[n-i-1],k-a[i],k-a[n-i-1]}));
        }
        int res=INT_MAX;
        sort(temp.begin(),temp.end());
        int val=abs(a[0]-a[n-1]);
        for(auto it: mp){
            val=lower_bound(temp.begin(),temp.end(),it.first)-temp.begin();
            val=2*val+(n/2-val);
            for(auto i: it.second){
                if(i<it.first){
                    val-=2;
                }
                else{
                    val-=1;
                }
            }
            res=min(val,res);
        }
        return res;
    }
};
