class Solution {
    class Pair{
        int x, y;
        Pair(int a, int b) {
            x = a;
            y = b;
        }
    }
    public int minChanges(int[] nums, int k) {
       int[] hd = new int[k+1]; 
       int[] diff = new int[k+1];

        int n = nums.length;
        int half = n/2;
        for(int i = 0; i < half; i++) {
            int j = n - i - 1;
            int d1 = Math.max(nums[i], k - nums[i]);
            int d2 = Math.max(nums[j], k - nums[j]);
            hd[Math.max(d1, d2)]++;
            diff[Math.abs(nums[i] - nums[j])]++;
        }

        int ans = n;
        int cs = 0;
        for(int i = 0; i < hd.length; i++) {
            cs += hd[i];
        }
        for(int X = 0; X <= k; X++) {
            int changes = 0;
            changes += (cs - diff[X]);

            changes += (half - cs)*2;
            ans = Math.min(ans, changes);
            cs -= hd[X];
        }

        return ans;




    }
}
