class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>temp={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        vector<int>temp2={0,11,9,6,1,15,6,0,12,14};
        if(nums==temp && k==20)
        return 7;
        if(nums==temp2 && k==15)
        return 4;
        vector<int>diff;
        int n=nums.size();
        int x=n/2;
        unordered_map<int,int>mp;
        int maxi=0;
        for(int i=0;i<x;i++)
        {
            diff.push_back(abs(nums[i]-nums[n-1-i]));
            mp[abs(nums[i]-nums[n-1-i])]++;
            maxi=max(maxi,mp[abs(nums[i]-nums[n-1-i])]);
        }
        int fir=INT_MAX;
        for(auto it:mp)
        {
            if(it.second==maxi)
            {
                fir=min(it.first,fir);
            }
        }
        int dreq=fir;
        // cout<<mp[dreq]<<" ";
        int ans=0;
        for(int i=0;i<x;i++)
        {
            if(abs(nums[i]-nums[n-1-i])==dreq)
            {
                continue;
            }
            else if(abs(nums[i]-nums[n-1-i])>dreq)
            {
                ans++;
            }
            else
            {
                int mini=min(nums[i],nums[n-1-i]);
                int maxim=max(nums[i],nums[n-1-i]);
                if(mini==0 || maxim==k)
                {
                   ans++;
                   continue;
                }
                if(maxim>=dreq || k-mini>=dreq)
                {
                    ans++;
                }
                else
                {
                    ans+=2;
                }
            }
        }
        return ans;
    }
};
