class Solution {
public:

    int minChanges(vector<int>& nums, int k) {
        int p1 = 0;
        int p2 = nums.size()-1;

        vector<pair<int,int>> v;
        map<int,int> mp;

        while(p1<p2){
            int d = abs(nums[p1]-nums[p2]);
            mp[d]++;
            p1++;
            p2--;
        }
        int mn = 0;
        int mn2 = 0;
        int d;
        int d2 = 0;
        for(auto i : mp){
            if(i.second>mn){
                d = i.first;
                mn = i.second;
            }
        }

        for(auto i : mp){
            if(i.second>mn2 && i.second<mn){
                d2 = i.first;
                mn2 = i.second;
            }
        }



        p1 = 0;
        p2 = nums.size()-1;

        int cnt = 0;

        cout << d << " " << d2 << '\n';
        while(p1<p2){
            int mn = min(nums[p1],nums[p2]);
            int mx = max(nums[p1],nums[p2]);

            cout << mx << " " << mn << '\n';

            if((mx-mn)!=d){
                if(mn+d>k && mx-d<0){
                    cnt+=2;
                }else{
                    cnt++;
                }
            }
            p1++;
            p2--;
        }

        int cnt2 = 0;

        p1 = 0;
        p2 = nums.size()-1;
        while(p1<p2){
            int mn = min(nums[p1],nums[p2]);
            int mx = max(nums[p1],nums[p2]);

            cout << mx << " " << mn << '\n';

            if((mx-mn)!=d2){
                if(mn+d2>k && mx-d2<0){
                    cnt2+=2;
                }else{
                    cnt2++;
                }
            }
            p1++;
            p2--;
        }

        return min(cnt,cnt2);
    }
};
