import collections

def process(nums, k):
    n = len(nums)
    s_dict = collections.defaultdict(int)

    for i in range(n // 2):
        num1 = nums[i]
        num2 = nums[n-i-1]
        s_dict[abs(num1- num2)] += 1


    ans = float("inf")
    # print(s_dict)
    for x in s_dict:
        if (n - s_dict[x]*2) // 2 > ans:
            continue
        tmp = 0
        for i in range(n // 2):
            num1 = nums[i]
            num2 = nums[n-i-1]
            if abs(num1 - num2) == x:
                continue
            if 0 <= x <= max(max(num1, num2), k-min(num1, num2)):
                tmp += 1
            else:
                tmp += 2
        ans = min(ans, tmp)
        # print(x, ans)
    return ans
class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        return process(nums, k)
