class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        // unordered_map<int,int> m;
        // int n=a.size();
        // for(int i=0;i<n/2;i++)
        // {
        //     int r=abs(a[i] - a[n - i - 1]);
        //     m[r]++;
        // }
        // int maxi=0;
        // for(auto &[a,b]:m)
        // {
        //     maxi=max(maxi,b);
        // }
        // return n-maxi;
    map<int,vector<int>> mp;
        vector<int> v;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i],b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            int X = max({a,b,k-a,k-b});
            mp[diff].push_back(X);
            v.push_back(X);
        }
        int res = nums.size();
        sort(v.begin(),v.end());

        for(auto &val:mp){

            int diff = val.first;
            int t = lower_bound(v.begin(),v.end(),diff)-v.begin();
            t = t*2+(nums.size()/2-t);

            for(auto &X:val.second){
                if(X<diff){
                    t-=2;
                }
                else{
                    t-=1;
                }
            }

            res = min(res,t);

        }

        return res;
    }
};
