class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>>mp;
        vector<int>v;
        for(int i=0;i<nums.size()/2;i++)
        {
            int num1=nums[i];
            int num2=nums[nums.size()-1-i];
            int diff=abs(num1-num2);
            int ans=max({num1,num2,k-num1,k-num2});
            mp[diff].push_back(ans);
            v.push_back(ans);
        }
        int size=nums.size();
        sort(v.begin(),v.end());
        for(auto &val:mp)
        {
            int diff=val.first;
            int t=lower_bound(v.begin(),v.end(),diff)-v.begin();
            t=t*2+(nums.size()/2-t);
            for(auto &temp:val.second)
            {
                if(temp<diff)
                t-=2;
                else
                t-=1;
            }
            size=min(size,t);
        }
        return size;
    }
};
