class Solution {
public:
    int solve(vector<int>& nums, int k, int diff) {
        int n = nums.size(), cnt = 0;
        for (int i = 0; i < n/2; ++i) {
            if (abs(nums[i] - nums[n-i-1]) == diff) continue;
            int x1 = nums[i] + diff;
            int x2 = nums[i] - diff;
            int x3 = nums[n-i-1] + diff;
            int x4 = nums[n-i-1] - diff;
            if (x1 >= 0 and x1 <= k) cnt++;
            else if (x2 >= 0 and x2 <= k) cnt++;
            else if (x3 >= 0 and x3 <= k) cnt++;
            else if (x4 >= 0 and x4 <= k) cnt++;
            else cnt += 2;
        }
        return cnt;
    }

    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();

        unordered_map<int,int> freq;
        for (int i = 0; i < n/2; ++i) 
            freq[abs(nums[i]-nums[n-i-1])]++;

        vector<pair<int,int>> sorted;
        for (auto it : freq)
            sorted.push_back({it.second, it.first});

        sort(sorted.rbegin(), sorted.rend());

        int ret = INT_MAX;
        for (int i = 0; i < sorted.size(); ++i) {
            int cur = solve(nums, k, sorted[i].second);
            if (ret < cur) return ret;
            else ret = cur;
        }

        return ret;        
    }
};

