class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int, int> Map;
        vector<int> v;

        for(int i = 0; i < n/2; i++)
        {
            int a = nums[i];
            int b = nums[n-1-i];
            int d = abs(a-b);
            Map[d]++;
            if(a > b) swap(a, b);
            v.push_back(max(k-a, b));
        }
        sort(v.begin(), v.end());

        int ans = n;
        cout<<ans<<endl;
        //for(auto& a:v) cout<<a<<" ";
        //cout<<endl;
        for(auto& [a,b]:Map)
        {
            int cur = n-2*b;
            auto it = lower_bound(v.begin(), v.end(), a);
            int left = it - v.begin();

            int right = n/2-left;
            right -= b;
            //cout<<a<<" "<<b<<" "<<left<<" "<<right<<endl;
            ans = min(ans, left*2+right);
        }
        return ans;
    }
};
