class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        int ans=n;
        vector<int>v1;
        map<int,vector<int>>mp;
        for(int i=0;i<nums.size()/2;i++){
            int r=nums[i];
            int s=nums[n-i-1];
            int x=max({r,s,k-r,k-s});
            v1.push_back(x);
            mp[abs(r-s)].push_back(x);
        }
        sort(v1.begin(),v1.end());
        for(auto it:mp){
            int sub=it.first;
            int l=lower_bound(v1.begin(),v1.end(),sub)-v1.begin();
            l+=(n/2);
            for(auto c:it.second){
                if(c<sub)l=l-2;
                else l--;
            }
            ans=min(ans,l);

        }
        return ans;


    }
};
