#include <vector>
#include <map>
#include <algorithm>
using namespace std;

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> mp;
        vector<int> v;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int d = abs(a - b);

            int X = max({a, b, k - a, k - b});

            mp[d].push_back(X);
            v.push_back(X);
        }

        sort(v.begin(), v.end());

        return solve(mp, v, nums.size());
    }

private:
    int solve(const map<int, vector<int>>& m, const vector<int>& v, int size) {
        int res = size;

        for (const auto& [d, values] : m) {

            int T = lower_bound(v.begin(), v.end(), d) - v.begin();
            T = T * 2 + (size / 2 - T);

            for (const auto& X : values) {
                if (X < d) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

            res = min(res, T);
        }

        return res;
    }
};
