    int minChanges(vector<int>& nums, int k) {
        vector<int> ovechkin = nums;
        int orr=k;
        int n = nums.size();
        map<int, vector<int>> mcdavid;
        vector<int> crosby;
        int i=0;
        while(i<n/2){
            int lindros = ovechkin[i];
            int yashin = ovechkin[ovechkin.size() - i - 1];

            mcdavid[abs(lindros - yashin)].push_back(max({lindros, yashin, orr - lindros, orr - yashin}));
            crosby.push_back(max({lindros, yashin, orr - lindros, orr - yashin}));
            i += 1;
        }

        int gretzky = cal(crosby, ovechkin, mcdavid);
        return gretzky;
    }
};
