class Solution {
public:
    int minChanges(vector<int>& nums, int ke) {
        int sz = nums.size();
        map<int,int> m;
        vector<int> v;
        for(int i=0;i<sz/2;i++)
        {
            m[abs(nums[i]-nums[sz-1-i])]++;
            v.push_back(max(ke-min(nums[i],nums[sz-1-i]),max(nums[i],nums[sz-1-i])));
        }
        sort(begin(v),end(v));
        int p = 0;
        int r = INT_MAX;
        for(auto [k,va] : m)
        {
            while(p<sz/2 && v[p]<k) p++;
            r = min(r,sz/2-va+(p));
        }
        return r;
    }
};
